import React from "react";
import { motion } from "framer-motion";
import ReactTypingEffect from "react-typing-effect";
import "../styles/Hero.css";
import { FaLinkedin, FaGithub } from "react-icons/fa";
import myPhoto from "../anmol.jpg"; // âœ… Correct way to import your image

function Hero() {
  return (
    <section className="hero">
      {/* Left Side - Image */}
      <motion.div
        className="hero-image"
        initial={{ x: -100, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <img src={myPhoto} alt="profile" />
      </motion.div>

      {/* Right Side - Info */}
      <motion.div
        className="hero-content"
        initial={{ x: 100, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <h1>
          Hi, I'm <span className="gradient-text">Anmol Ashok Nandavadekar</span>
        </h1>

        {/* Typing Effect */}
        <ReactTypingEffect
          text={["ðŸ’» I'm a Software Developer", "ðŸš€ I build AI-powered Web Experiences"]}
          speed={80}
          eraseSpeed={50}
          typingDelay={300}
          className="typing-text"
        />

        {/* Buttons */}
        <div className="hero-buttons">
          <a href="/resume.pdf" download>
            <button className="btn-primary">ðŸ“„ Download Resume</button>
          </a>
          <a href="#projects">
            <button className="btn-secondary">ðŸ’» View Projects</button>
          </a>
        </div>

        {/* Floating Social Icons */}
        <div className="social-icons">
          <a href="https://linkedin.com" target="_blank" rel="noreferrer">
            <FaLinkedin />
          </a>
          <a href="https://github.com" target="_blank" rel="noreferrer">
            <FaGithub />
          </a>
        </div>
      </motion.div>
    </section>
  );
}

export default Hero;
